import React from 'react'
import {connect} from 'react-redux'
import { Grid, Image, Segment, Button } from 'semantic-ui-react'
import Calendar from './Calendar'


class Portfolio extends React.Component {


handleBackClick = () => {
    localStorage.removeItem('searched_name')
    localStorage.removeItem('searched_img')
    console.log('back', localStorage)

}


    render(){

        return (
            <Grid>
            <Grid.Column width={4}>
              <Image src={localStorage.searched_img}/>
            </Grid.Column>
            <Grid.Column width={9}>
              <Segment.Group raised>
                 <Segment>Name: {localStorage.searched_name}</Segment>
                 {/* <Segment>Specialty: {this.props.searchedUser.specialty}</Segment>
                 <Segment>Board Certified? {this.props.searchedUser.board_certified? "Yes" : "No"}</Segment>
                 <Segment>Email: {this.props.searchedUser.email}</Segment>
                 <Segment>Contact number: {this.props.searchedUser.phone_number}</Segment>
                 <Segment>Address: {this.props.searchedUser.address? this.props.searchedUser.address:"n/a"}</Segment> */}
                 <Segment><Calendar/><Button className="ui teal button">Schedule</Button><Button onClick={this.handleBackClick}className="ui teal button">Back</Button></Segment>
              </Segment.Group>
            </Grid.Column>
          </Grid>
        )
    }
}


const mapStateToProps = (state) => {
    return {
        searchedUser: state.searchedUser
    }
}


export default connect(mapStateToProps)(Portfolio)
