const initialState = {
    token: "",
    id: "",
    user: ""
}

const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'SET_TOKEN':
            return {
                ...state,
                token: action.token,
                id: action.id,
            }
        case 'SET_USER':
            return {
                ...state,
                user: action.user

            }
        case 'LOG_OUT':
            return {
                ...initialState
            }
        case 'SET_CATEGORY':

            return {
             ...state,
             category: action.category
            }
        case 'UPDATE_SUCCESS':
            return {
                ...state,
                updated: action.updated
            }
        case 'CHANGE_UPDATE_STATE':
            return {
                ...state,
                updated: action.updated
            }

        case 'DATE':
            return {
                ...state,
                date: action.date
            }

        case 'PICKED_USER':
            return {
                ...state,
                pickedId: action.pickedId
            }

        case 'SEARCH_SOURCE':
            return {
                ...state,
                source: action.source
            }

        case 'SEARCHED_USER':
            return{
                ...state,
                searchedUser: action.searchedUser
            }

        case 'SET_SEARCHED_VALUE':
            return {
                ...state,
                searchedValue: action.value
            }

        case 'SET_CLIENTS':
            return {
                ...state,
                clients: action.clients
            }

        case 'SET_PROVIDERS':
            return {
                ...state,
                providers: action.providers
            }
        default:
           return state
    }

}

export default userReducer
