import PropTypes from 'prop-types'
import _ from 'lodash'
import faker from 'faker'
import React, { Component } from 'react'
import { Search, Grid, Header, Segment, Label } from 'semantic-ui-react'
import { connect } from 'react-redux'
import {searchSource} from '../reducer/actions'


const resultRenderer = ({ title }) => {

return <Label content={title} />}

resultRenderer.propTypes = {
  title: PropTypes.string,
  description: PropTypes.string,
}

const initialState = { isLoading: false, results: [], value: '' }

class SearchBar extends Component {
  state = initialState

  handleResultSelect = (e, { result }) => {
      console.log(e.target, result)
      this.setState({ value: result.title })
    }

  handleSearchChange = (e, { value }) => {
    this.setState({ isLoading: true, value })
    this.props.searchSource()

    setTimeout(() => {

      if (this.state.value.length < 1) return this.setState(initialState)

      const re = new RegExp(_.escapeRegExp(this.state.value), 'i')
      const isMatch = (result) => {
          console.log(re.test(result.title))
          re.test(result.title)}

      this.setState({
        isLoading: false,
        results: _.filter(source, isMatch),
      })
    }, 300)
  }

  render() {
    const { isLoading, value, results } = this.state

    return (
      <Grid>
        <Grid.Column width={6}>
          <Search
            loading={isLoading}
            onResultSelect={this.handleResultSelect}
            onSearchChange={_.debounce(this.handleSearchChange, 500, {
              leading: true,
            })}
            results={results}
            value={value}
            resultRenderer={resultRenderer}
            {...this.props}
          />
        </Grid.Column>
        <Grid.Column width={10}>
          <Segment>
            <Header>State</Header>
            <pre style={{ overflowX: 'auto' }}>
              {JSON.stringify(this.state, null, 2)}
            </pre>
            <Header>Options</Header>
            <pre style={{ overflowX: 'auto' }}>
              {JSON.stringify(this.props.source, null, 2)}
            </pre>
          </Segment>
        </Grid.Column>
      </Grid>
    )
  }
}


const mapStateToProps = (state) => {
    return {
        source: state.source
    } 
}

export default connect(mapStateToProps, {searchSource})(SearchBar)
