const initialState = {
    token: "",
    id: "",
    user: ""
}

const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'SET_TOKEN':
            return {
                ...state,
                token: action.token,
                id: action.id,
            }
        case 'SET_USER':
            return {
                ...state,
                user: action.user

            }
        case 'LOG_OUT':
            return {
                ...initialState
            }
        case 'SET_CATEGORY':

            return {
             ...state,
             category: action.category
            }
        case 'UPDATE_SUCCESS':
            return {
                ...state,
                updated: action.updated
            }
        case 'CHANGE_UPDATE_STATE':
            return {
                ...state,
                updated: action.updated
            }

        case 'DATE':
            return {
                ...state,
                date: action.date
            }

        case 'PICKED_USER':
            return {
                ...state,
                pickedId: action.pickedId
            }
        default:
           return state
    }

}

export default userReducer
