import React from 'react'
import { connect } from 'react-redux';
import './Profile.css'
import {Link} from 'react-router-dom'
import { LogOut } from '../reducer/actions'


class Profile extends React.Component {

 state={
     clicked: false
 }

handleLogOutClick = () => {

    localStorage.removeItem("token")
    localStorage.removeItem("id")

    this.props.LogOut()

}

handleClick = () => {
    console.log('clicked')
    this.setState({
        clicked: !this.state.clicked
    })
}


    render(){
     console.log(localStorage, this.props.user.img_url, this.props.user.username)
        return (
            this.state.clicked?



            <div  onClick={this.handleClick} className="ui raised link card">
                 <div className="content">
                   <a className="header">Username: {this.props.user.username}</a>
                 </div>

            </div>
           :
           <div  className="ui raised link card">
             <div onClick={this.handleClick} className="image">
               <img src={this.props.user.img_url} alt={this.props.user.username}/>
             </div>
             <div className="content">
               <a className="header">{this.props.user.username}</a>
             </div>
            <Link to="/"><button onClick={this.handleLogOutClick} className="ui teal button">Logout</button></Link>
       </div>


        )
    }
}

const mapStateToProps = (state) => {
    return {
       user: state.user
    }
}

export default connect(mapStateToProps, {LogOut})(Profile)
