import React from 'react'
import { connect } from 'react-redux'
import {Search} from 'semantic-ui-react'
import {newSource} from '../reducer/actions'

class SearchBar extends React.Component {

  state = {
      isLoading: false,
      results: [],
      value: ''

  }


  handleSearchChange = (evt) => {

      this.setState({
          ...this.state,
          value: evt.target.value.toLowerCase()
      })

     if(this.state.value){
         this.props.newSource()
     }

   const sleep = milliseconds => {
       return new Promise(resolve => setTimeout (resolve, milliseconds))
   }
   sleep(1000).then(() => {
        const newSource = this.props.source.map(object => {
            return { title: object.first_name.toLowerCase() + " " + object.last_name.toLowerCase(), ...object}
        })

        const results = newSource.filter((obj) => {
             return obj.title.includes(this.state.value)

        })
        this.setState({
            ...this.state,
            results: results
        })
   }
   )


  }



render(){

    return (
        this.props.user.specialty?
        <Search onSearchChange={this.handleSearchChange} placeholder='Find your client by name'/>
        :
        <Search onSearchChange={this.handleSearchChange} placeholder='Find your provider by name'/>


    )
}

}

const mapStateToProps = (state) => {
    return {
        user: state.user,
        source: state.source
    }
}


export default connect(mapStateToProps, {newSource})(SearchBar)
