        return(
            <Grid><GridColumn className="provider img text">Please find your providers or clients</GridColumn></Grid>
        )
    }else{
        return(
            <Grid><GridColumn className="provider img text">Please click on picture to choose</GridColumn></Grid>
        )
    }
}



